const TaskList = ({status}) => {
	let title = '';
	switch(status) {
		case 'backlog':
			title = 'Backlog';
			break;
		case 'in-process':
			title = 'In process';
			break;
		case 'ready':
			title = 'Ready';
			break;
		case 'remove':
			title = 'Remove';
			break;
		default:
			break;
	}
	return (
		<section className={`tasks tasks--${status}`}>
      <h3 className="tasks__title">{title}</h3>
			<ul className="tasks__list">
				{status === "in-process" &&
					<li className="tasks__item tasks__item-empty">Drag the card</li>
				}
				{status === "ready" &&
					<li className="tasks__item tasks__item-empty">Drag the card</li>
				}
				<li className="tasks__item"><p>First Task <button className="btn--edit"></button></p></li>
			</ul>
		</section>
	)
}

export default TaskList;
