const TaskList = ({status}) => {
	let title = '';
	switch(status) {
		case 'backlog':
			title = 'Backlog';
			break;
		case 'in-process':
			title = 'In process';
			break;
		case 'ready':
			title = 'Ready';
			break;
		case 'remove':
			title = 'Remove';
			break;
	}
	return (
		<section className={`tasks tasks--${status}`}>
      <h3 className="tasks__title">{title}</h3>
			<ul className="tasks__list">
				<li className="tasks__item">First Task <button className="btn--edit"><svg width="24" height="24" viewBox="0 10 24 24" xmlns="http://www.w3.org/2000/svg">
  <path fill-rule="evenodd" clip-rule="evenodd"
        d="M18.5325 7.22254C18.825 7.51504 18.825 7.98754 18.5325 8.28004L17.16 9.65254L14.3475 6.84004L15.72 5.46754C16.0125 5.17504 16.485 5.17504 16.7775 5.46754L18.5325 7.22254ZM5.25 18.75V15.9375L13.545 7.64254L16.3575 10.455L8.0625 18.75H5.25Z"
        fill="#D0D0D0"/>
</svg></button></li>
			</ul>
		</section>
	)
}

export default TaskList;
